package whilelooping;

public class DoWhileLooping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=30;
		
		
		/*while(num>=10)
		{
			System.out.println(num);
			num--;
		}*/
		do
		{
			System.out.print(num+" ");//30
			num--;//num=29
			
		}while(num<=10);
		
		System.out.println("Done");

	}

}
