package whilelooping;

public class WhileLooping1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1, n=5;
		while(i<=n)                 // i=1   i=2   i=3 i=4  i=5 i=6
		{
			System.out.print(i+" ");// 1 2 3 4 5
			i++;                  //
		}
		System.out.println("Done");

	}

}
